INSERT INTO `wp_posts` VALUES (4, 1, '2009-12-01 10:24:24', '2009-12-01 10:24:24', 'Many American recipes call for buttermilk in cakes and muffins but if I can\'t be bothered looking for it around Brighton (Infinity Foods is a good source) I can always make my own:\r\n\r\nRecipe:\r\n<ul>\r\n	<li>1 cup minus 1 Tbsp of whole milk</li>\r\n	<li>1 Tbsp lemon juice or vinegar</li>\r\n	<li>Let stand for 5 minutes</li>\r\n</ul>\r\nLet stand for 5 minutes and voila!', 'Buttermilk substitute', '', 'publish', 'open', 'open', '', 'buttermilk-substitute', '', '', '2010-10-13 13:19:05', '2010-10-13 13:19:05', '', 0, 'http://localhost:8000/bakerofbrighton/?p=4', 0, 'post', '', 0) ; 
