INSERT INTO `wp_posts` VALUES (8, 1, '2009-12-01 10:27:30', '2009-12-01 10:27:30', 'I used to dismiss the call for cake flour in recipes as a bit of a pretension - it also seems impossible to find it in your everyday supermarket! However, cake flour really does make cakes lighter and more delicate. Here is the home-made version:\r\n\r\nRecipe:\r\n<ul>\r\n	<li>1 cup minus 3 Tbsp flour</li>\r\n	<li>3 Tbsp corn or potato flour</li>\r\n</ul>', 'Home-made cake flour', '', 'publish', 'open', 'open', '', 'home-made-cake-flour', '', '', '2009-12-21 15:24:54', '2009-12-21 15:24:54', '', 0, 'http://localhost:8000/bakerofbrighton/?p=8', 0, 'post', '', 1) ; 
